public class Calculator {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		long a=100, b=100;
		System.out.println("The inputs are " + a + " " + b);
		System.out.println("Adding values "+ (a+b));
		System.out.println("Subtract values "+ (a-b));
		System.out.println("Multiply values "+ (a*b));
		System.out.println("Divide values "+ (a/b));
		
		
	}

}
